import React,{useState} from "react";
import Header from "./Header";
import './Login.css';

const Login = () => {
    const [isLogin,setIsLogin]=useState(true);
    const [email,setEmail]=useState("");
    const [password,setPassword]=useState("");
    const [fullname,setFullname]=useState("");
    const logininHandler=()=>{
        setIsLogin(!isLogin);
    }
    const getInputData=(e)=>{
         e.preventDefault();
         console.log(fullname,email,password);
            }
return (
<div>
            <Header/>
            <div className="login-banner">
                <img src="https://assets.nflxext.com/ffe/siteui/vlv3/dc1cf82d-97c9-409f-b7c8-6ac1718946d6/14a8fe85-b6f4-4c06-8eaf-eccf3276d557/IN-en-20230911-popsignuptwoweeks-perspective_alpha_website_medium.jpg" alt="banner" className="login-banner"/>
            </div>
            <form className="login-form" onSubmit={getInputData}>
                    <h1>{isLogin ? "Login" : "Sign up"}</h1>
                    <div className="login-form-container">
                            {
                                    !isLogin && <input value={fullname} onChange={(e) => setFullname(e.target.value)} type='text' placeholder="fullname" />
                            }
                            <input value={email} onChange={(e) => setEmail(e.target.value)} type='email' placeholder="email"/>
                            <input value={password} onChange={(e) => setPassword(e.target.value)} type='password' placeholder="password"/>
                            <button className="login-button" type="submit">{isLogin ? "Login" : "Sign up"}</button>
                            <p>{isLogin ? "New to Netflix?" : "Already have an account?"}<span onClick={logininHandler} style={{cursor: "pointer", color: "blue", marginLeft: "5px"}}>{isLogin ? "Signin" : "Login"}</span></p>
                    </div>
            </form>
</div>
);
};

export default Login;
